import { useState } from "react";
import {BaseColaboradores} from './ListaColaboradores'

const Body = () => {
    const [nombreTarea, setNombreTarea] = useState("")
    const [BaseColaboradore, setBaseColaboradore] = useState(BaseColaboradores)

    const enviarFormulario = (e) => {
        e.preventDefault()
        setBaseColaboradore([BaseColaboradore, {nombreTarea, completada: false}])
        console.log(BaseColaboradore)
    }

    const capturaInput = (e) => {
        setNombreTarea(e.target.value)
    }

    const eliminarTarea = nombre => {
        const tareasFiltradas = BaseColaboradore.filter(tarea=> tarea.nombre !== nombre)
        setBaseColaboradore(tareasFiltradas)
    }

    return (
       <div className="container">
            <form onSubmit={enviarFormulario} className="formulario">
                <input 
                    name="nombreTarea" 
                    onChange={capturaInput} 
                    className="form-control"
                    value={nombreTarea}
                />
                <button variant="info" className="btn btn-info"> Agregar Tarea</button>
            </form>

            <ul className="lista">
                <li key={tarea.nombre} onClick={() => eliminarTarea(tarea.nombre)}>{tarea}</li>
            </ul>
       </div>
    )
};
export default Body;
